package org.rcosjava.messaging.messages;import org.rcosjava.messaging.postoffices.PostOffice;import org.rcosjava.messaging.postoffices.SimpleMessageHandler;/** * Removing all message handlers (when post office is removed). <P> * * * * @author Andrew Newman * @created 1st July 1996 * @version 1.00 $Date$ */public class RemoveAllHandlers extends MessageAdapter{  /**   * Creates a message with a known body (should be serializable) and a known   * source.   *   * @param newSource sets the source of the message.   * @param newBody the message contents.   */  public RemoveAllHandlers(SimpleMessageHandler newSource, Object newBody)  {    super(newSource, newBody);  }  /**   * Upon receiving the message the post office clearHandlers is called.   *   * @param theElement the post office to remove all the handlers from.   */  public void doMessage(PostOffice theElement)  {    theElement.clearHandlers();  }}