package org.rcosjava.messaging.messages.os;import org.rcosjava.messaging.postoffices.os.OSMessageHandler;import org.rcosjava.software.filesystem.FileSystemManager;/** * Description of the Class * * @author administrator * @created 28 April 2002 */public class RegisterFileSystem extends OSMessageAdapter{  /**   * Description of the Field   */  private String sFSType;  /**   * Description of the Field   */  private String sFSName;  /**   * Constructor for the RegisterFileSystem object   *   * @param theSource Description of Parameter   * @param sNewFSType Description of Parameter   * @param sNewFSName Description of Parameter   */  public RegisterFileSystem(OSMessageHandler theSource, String sNewFSType,      String sNewFSName)  {    super(theSource);    sFSType = sNewFSType;    sFSName = sNewFSName;  }  /**   * Sets the FileSystemIDs attribute of the RegisterFileSystem object   *   * @param sNewFSType The new FileSystemIDs value   * @param sNewFSName The new FileSystemIDs value   */  public void setFileSystemIDs(String sNewFSType, String sNewFSName)  {    sFSType = sNewFSType;    sFSName = sNewFSName;  }  /**   * Description of the Method   *   * @param theElement Description of Parameter   */  public void doMessage(FileSystemManager theElement)  {    theElement.register(sFSType, sFSName);  }}