package org.rcosjava.messaging.messages.os;import org.rcosjava.messaging.postoffices.os.OSMessageHandler;import org.rcosjava.software.ipc.IPC;/** * Message sent to IPC manager to create a new semaphore. <P> * * * <DT> <B>History:</B> * <DD> 08/08/98 Changed to new message system. AN </DD> </DT> <P> * * * * @author Andrew Newman. * @author Bruce Jamieson. * @created 28th of March 1996 * @version 1.00 $Date$ */public class SemaphoreCreate extends OSMessageAdapter{  /**   * Description of the Field   */  private String semaphoreId;  /**   * Description of the Field   */  private int pid;  /**   * Description of the Field   */  private int value;  /**   * Constructor for the SemaphoreCreate object   *   * @param theSource Description of Parameter   * @param newSemaphoreId Description of Parameter   * @param newPID Description of Parameter   * @param newValue Description of Parameter   */  public SemaphoreCreate(OSMessageHandler theSource, String newSemaphoreId,      int newPID, int newValue)  {    super(theSource);    semaphoreId = newSemaphoreId;    pid = newPID;    value = newValue;  }  /**   * Sets the SemaphoreID attribute of the SemaphoreCreate object   *   * @param newSemaphoreId The new SemaphoreID value   */  public void setSemaphoreID(String newSemaphoreId)  {    semaphoreId = newSemaphoreId;  }  /**   * Sets the ProcessID attribute of the SemaphoreCreate object   *   * @param newProcessID The new ProcessID value   */  public void setProcessID(int newProcessID)  {    pid = newProcessID;  }  /**   * Sets the SemaphoreValue attribute of the SemaphoreCreate object   *   * @param newValue The new SemaphoreValue value   */  public void setSemaphoreValue(int newValue)  {    value = newValue;  }  /**   * Description of the Method   *   * @param theElement Description of Parameter   */  public void doMessage(IPC theElement)  {    theElement.semaphoreCreate(semaphoreId, pid, value);  }}