package org.rcosjava.messaging.messages.os;import org.rcosjava.messaging.postoffices.os.OSMessageHandler;import org.rcosjava.software.ipc.IPC;/** * Message sent to IPC manager for signal semaphore type messages. <P> * * * * @author Andrew Newman. * @created 11th of August 1998 * @version 1.00 $Date$ */public class SemaphoreSignal extends OSMessageAdapter{  /**   * Description of the Field   */  private int semaphoreId;  /**   * Description of the Field   */  private int pid;  /**   * Constructor for the SemaphoreSignal object   *   * @param theSource Description of Parameter   * @param newSemaphoreId Description of Parameter   * @param newPID Description of Parameter   */  public SemaphoreSignal(OSMessageHandler theSource,      int newSemaphoreId, int newPID)  {    super(theSource);    semaphoreId = newSemaphoreId;    pid = newPID;  }  /**   * Sets the SemaphoreID attribute of the SemaphoreSignal object   *   * @param newSemaphoreId The new SemaphoreID value   */  public void setSemaphoreID(int newSemaphoreId)  {    semaphoreId = newSemaphoreId;  }  /**   * Sets the ProcessID attribute of the SemaphoreSignal object   *   * @param newPID The new ProcessID value   */  public void setProcessID(int newPID)  {    pid = newPID;  }  /**   * Description of the Method   *   * @param theElement Description of Parameter   */  public void doMessage(IPC theElement)  {    theElement.sempahoreSignal(semaphoreId, pid);  }}