package org.rcosjava.messaging.messages.os;import org.rcosjava.messaging.postoffices.os.OSMessageHandler;import org.rcosjava.software.ipc.IPC;/** * Description of the Class * * @author administrator * @created 28 April 2002 */public class SemaphoreWait extends OSMessageAdapter{  /**   * Description of the Field   */  private int iSemaphoreID;  /**   * Description of the Field   */  private int iPID;  /**   * Constructor for the SemaphoreWait object   *   * @param theSource Description of Parameter   * @param iNewSemaphoreID Description of Parameter   * @param iNewPID Description of Parameter   */  public SemaphoreWait(OSMessageHandler theSource,      int iNewSemaphoreID, int iNewPID)  {    super(theSource);    iSemaphoreID = iNewSemaphoreID;    iPID = iNewPID;  }  /**   * Sets the SemaphoreID attribute of the SemaphoreWait object   *   * @param iNewSemaphoreID The new SemaphoreID value   */  public void setSemaphoreID(int iNewSemaphoreID)  {    iSemaphoreID = iNewSemaphoreID;  }  /**   * Sets the ProcessID attribute of the SemaphoreWait object   *   * @param iNewPID The new ProcessID value   */  public void setProcessID(int iNewPID)  {    iPID = iNewPID;  }  /**   * Description of the Method   *   * @param theElement Description of Parameter   */  public void doMessage(IPC theElement)  {    theElement.semaphoreWait(iSemaphoreID, iPID);  }}