package org.rcosjava.messaging.messages.os;import org.rcosjava.messaging.postoffices.os.OSMessageHandler;import org.rcosjava.software.ipc.IPC;/** * Message sent to IPC manager to read a bit of the shared memory structure. <P> * * * <DT> <B>History:</B> * <DD> 08/08/98 Changed to new message system. AN </DD> </DT> <P> * * * * @author Andrew Newman. * @author Bruce Jamieson. * @created 30th of March 1996 * @version 1.00 $Date$ */public class SharedMemoryCloseMessage extends OSMessageAdapter{  /**   * The id of the shared memory structure.   */  private int sharedMemoryId;  /**   * The process id closing the share memory structure.   */  private int PID;  /**   * Constructor for the SharedMemoryCloseMessage object   *   * @param theSource Description of Parameter   * @param newSharedMemoryId Description of Parameter   * @param newPID Description of Parameter   */  public SharedMemoryCloseMessage(OSMessageHandler theSource,      int newSharedMemoryId, int newPID)  {    super(theSource);    sharedMemoryId = newSharedMemoryId;    PID = newPID;  }  /**   * Call sharedMemorySize on the IPC.   *   * @param theElement the IPC to call sharedMemorySize on.   */  public void doMessage(IPC theElement)  {    theElement.sharedMemoryClose(sharedMemoryId, PID);  }}