package org.rcosjava.messaging.messages.os;import org.rcosjava.messaging.postoffices.os.OSMessageHandler;import org.rcosjava.software.ipc.IPC;/** * Message sent to IPC manager for the size of a shared memory structure. <P> * * * <DT> <B>History:</B> * <DD> 08/08/98 Changed to new message system. AN </DD> </DT> <P> * * * * @author Andrew Newman. * @author Bruce Jamieson. * @created 30th of March 1996 * @version 1.00 $Date$ */public class SharedMemorySizeMessage extends OSMessageAdapter{  /**   * The name of the new shared memory structured.   */  private int sharedMemoryId;  /**   * Constructor for the SharedMemorySizeMessage object   *   * @param theSource Description of Parameter   * @param newSharedMemoryId Description of Parameter   */  public SharedMemorySizeMessage(OSMessageHandler theSource,      int newSharedMemoryId)  {    super(theSource);    sharedMemoryId = newSharedMemoryId;  }  /**   * Call sharedMemorySize on the IPC.   *   * @param theElement the IPC to call sharedMemorySize on.   */  public void doMessage(IPC theElement)  {    theElement.sharedMemorySize(sharedMemoryId);  }}