package org.rcosjava.messaging.messages.universal;import org.rcosjava.messaging.postoffices.os.OSMessageHandler;import org.rcosjava.software.animator.ipc.IPCManagerAnimator;import org.rcosjava.software.memory.MemoryRequest;/** * Description of the Class * * @author administrator * @created 28 April 2002 */public class FinishedMemoryWrite extends UniversalMessageAdapter{  /**   * Description of the Field   */  private MemoryRequest mrRequest;  /**   * Constructor for the FinishedMemoryWrite object   *   * @param theSource Description of Parameter   * @param newRequest Description of Parameter   */  public FinishedMemoryWrite(OSMessageHandler theSource, MemoryRequest newRequest)  {    super(theSource);    mrRequest = newRequest;  }  /**   * Description of the Method   *   * @param theElement Description of Parameter   */  public void doMessage(IPCManagerAnimator theElement)  {    theElement.finishedWritingMemory(this.mrRequest);  }  /**   * Description of the Method   *   * @return Description of the Returned Value   */  public boolean undoableMessage()  {    return false;  }}