package org.rcosjava.messaging.messages.universal;import org.rcosjava.messaging.postoffices.os.OSMessageHandler;import org.rcosjava.software.animator.process.ProcessSchedulerAnimator;import org.rcosjava.software.process.ProcessScheduler;/** * Description of the Class * * @author administrator * @created 28 April 2002 */public class ProcessToTerminalAllocation extends UniversalMessageAdapter{  /**   * Description of the Field   */  private String sTerminalID;  /**   * Description of the Field   */  private int iPID;  /**   * Constructor for the ProcessToTerminalAllocation object   *   * @param theSource Description of Parameter   * @param sTerminalID Description of Parameter   * @param iPID Description of Parameter   */  public ProcessToTerminalAllocation(OSMessageHandler theSource, String sTerminalID, int iPID)  {    super(theSource);    this.sTerminalID = sTerminalID;    this.iPID = iPID;  }  /**   * Description of the Method   *   * @param theElement Description of Parameter   */  public void doMessage(ProcessScheduler theElement)  {    theElement.processAllocatedTerminal(iPID, sTerminalID);  }  /**   * Description of the Method   *   * @param theElement Description of Parameter   */  public void doMessage(ProcessSchedulerAnimator theElement)  {    theElement.zombieToReady(iPID);  }}