package org.rcosjava.messaging.messages.universal;import org.rcosjava.messaging.postoffices.os.OSMessageHandler;import org.rcosjava.software.animator.ipc.IPCManagerAnimator;import org.rcosjava.software.memory.MemoryManager;import org.rcosjava.software.memory.MemoryRequest;/** * Description of the Class * * @author administrator * @created 28 April 2002 */public class ReadPage extends UniversalMessageAdapter{  /**   * Description of the Field   */  private MemoryRequest mrRequest;  /**   * Constructor for the ReadPage object   *   * @param theSource Description of Parameter   * @param mrNewRequest Description of Parameter   */  public ReadPage(OSMessageHandler theSource,      MemoryRequest mrNewRequest)  {    super(theSource);    mrRequest = mrNewRequest;  }  /**   * Description of the Method   *   * @param theElement Description of Parameter   */  public void doMessage(IPCManagerAnimator theElement)  {    theElement.readingMemory(mrRequest);  }  /**   * Description of the Method   *   * @param theElement Description of Parameter   */  public void doMessage(MemoryManager theElement)  {    mrRequest.setMemory(theElement.readPage(mrRequest.getPID(),        mrRequest.getMemoryType(), mrRequest.getOffset()));    FinishedMemoryRead aMessage = new FinishedMemoryRead(theElement, mrRequest);    theElement.sendMessage(aMessage);  }}