package org.rcosjava.messaging.messages.universal;import org.rcosjava.messaging.postoffices.os.OSMessageHandler;import org.rcosjava.software.animator.process.ProcessSchedulerAnimator;import org.rcosjava.software.process.ProcessScheduler;import org.rcosjava.software.process.RCOSProcess;/** * Description of the Class * * @author administrator * @created 28 April 2002 */public class RunningToReady extends UniversalMessageAdapter{  /**   * Description of the Field   */  private RCOSProcess myProcess;  /**   * Constructor for the RunningToReady object   *   * @param theSource Description of Parameter   * @param newProcess Description of Parameter   */  public RunningToReady(OSMessageHandler theSource, RCOSProcess newProcess)  {    super(theSource);    myProcess = newProcess;  }  /**   * Description of the Method   *   * @param theElement Description of Parameter   */  public void doMessage(ProcessScheduler theElement)  {    theElement.runningToReady(myProcess);  }  /**   * Description of the Method   *   * @param theElement Description of Parameter   */  public void doMessage(ProcessSchedulerAnimator theElement)  {    theElement.cpuToReady(myProcess.getPID());  }}