package org.rcosjava.messaging.messages.universal;import org.rcosjava.hardware.cpu.Context;import org.rcosjava.messaging.postoffices.os.OSMessageHandler;import org.rcosjava.software.animator.cpu.CPUAnimator;/** * Sets current CPU status of BP, SP, etc. <P> * * * * @author Andrew Newman. * @created 1st January 1998 * @version 1.00 $Date$ */public class SetContext extends UniversalMessageAdapter{  /**   * Description of the Field   */  private Context myContext;  /**   * Constructor for the SetContext object   *   * @param theSource Description of Parameter   * @param newContext Description of Parameter   */  public SetContext(OSMessageHandler theSource,      Context newContext)  {    super(theSource);    myContext = newContext;  }  /**   * Calls setContext on the CPU Animator to display the stack pointer, base   * pointer, etc.   *   * @param theElement the CPU Animator to do the work on.   */  public void doMessage(CPUAnimator theElement)  {    theElement.setContext(myContext);  }}