package org.rcosjava.messaging.messages.universal;import org.rcosjava.messaging.postoffices.animator.AnimatorMessageHandler;import org.rcosjava.software.process.ProcessScheduler;/** * Sets the priority of a given process. <P> * * * * @author Andrew Newman. * @created 8th of May 2001 * @version 1.00 $Date$ */public class SetProcessPriority extends UniversalMessageAdapter{  /**   * Description of the Field   */  private int processId;  /**   * Description of the Field   */  private int priority;  /**   * Constructor for the SetProcessPriority object   *   * @param theSource Description of Parameter   * @param newProcessId Description of Parameter   * @param newPriority Description of Parameter   */  public SetProcessPriority(AnimatorMessageHandler theSource,      int newProcessId, int newPriority)  {    super(theSource);    processId = newProcessId;    priority = newPriority;  }  /**   * Description of the Method   *   * @param theElement Description of Parameter   */  public void doMessage(ProcessScheduler theElement)  {    theElement.setProcessPriority(processId, priority);  }}