package org.rcosjava.messaging.messages.universal;import org.rcosjava.messaging.postoffices.os.OSMessageHandler;import org.rcosjava.hardware.memory.Memory;import org.rcosjava.software.animator.ipc.IPCManagerAnimator;import org.rcosjava.software.ipc.SharedMemory;import org.rcosjava.software.memory.MemoryReturn;/** * Message sent to IPC manager animator for verification of created shared * memory block. * <P> * @author Andrew Newman. * @created 17th of April 2001 * @version 1.00 $Date$ */public class SharedMemoryCreated extends UniversalMessageAdapter{  /**   * The name of the new shared memory structured.   */  private String sharedMemoryId;  /**   * The memory created.   */  private MemoryReturn memoryReturn;  private Memory memory;  /**   * Constructor for the SharedMemoryCreated object   *   * @param theSource who sent the message.   * @param newSharedMemoryId the name of the shared memory segment.   * @param newMemory the memory type, process id, etc.   */  public SharedMemoryCreated(OSMessageHandler theSource,      String newSharedMemoryId, MemoryReturn newMemoryReturn, Memory newMemory)  {    super(theSource);    sharedMemoryId = newSharedMemoryId;    memoryReturn = newMemoryReturn;    memory = newMemory;  }  /**   * Calls the sharedMemoryCreated method to indicate that the element was   * created.   *   * @param theElement Description of Parameter   */  public void doMessage(IPCManagerAnimator theElement)  {    theElement.sharedMemoryCreated(sharedMemoryId, memoryReturn, memory);  }}