package org.rcosjava.messaging.messages.universal;import org.rcosjava.messaging.postoffices.animator.AnimatorMessageHandler;import org.rcosjava.software.process.ProgramManager;/** * Message to stop the execution of code. <P> * * * * @author Andrew Newman. * @created 28th of March 1998 * @version 1.00 $Date$ */public class Stop extends UniversalMessageAdapter{  /**   * Constructor for the Stop object   *   * @param theSource Description of Parameter   */  public Stop(AnimatorMessageHandler theSource)  {    super(theSource);  }  /**   * Description of the Method   *   * @param theElement Description of Parameter   */  public void doMessage(ProgramManager theElement)  {    theElement.stopThread();  }}