package org.rcosjava.messaging.messages.universal;import org.rcosjava.messaging.postoffices.animator.AnimatorMessageHandler;import org.rcosjava.messaging.postoffices.os.OSMessageHandler;import org.rcosjava.software.animator.terminal.TerminalManagerAnimator;import org.rcosjava.software.terminal.TerminalManager;/** * Move terminal to front. <P> * * * * @author Andrew Newman. * @created 24th March 1996 * @version 1.00 $Date$ */public class TerminalFront extends UniversalMessageAdapter{  /**   * Description of the Field   */  private int terminalNo = 0;  /**   * Description of the Field   */  private boolean forAnimator = false;  /**   * Constructor for the TerminalFront object   *   * @param theSource Description of Parameter   * @param newTerminalNo Description of Parameter   * @param newForAnimator Description of Parameter   */  public TerminalFront(OSMessageHandler theSource, int newTerminalNo,      boolean newForAnimator)  {    super(theSource);    terminalNo = newTerminalNo;    forAnimator = newForAnimator;  }  /**   * Constructor for the TerminalFront object   *   * @param theSource Description of Parameter   * @param newTerminalNo Description of Parameter   */  public TerminalFront(AnimatorMessageHandler theSource, int newTerminalNo)  {    super(theSource);    terminalNo = newTerminalNo;  }  /**   * Sets the TerminalNumber attribute of the TerminalFront object   *   * @param newTerminalNo The new TerminalNumber value   */  public void setTerminalNumber(int newTerminalNo)  {    terminalNo = newTerminalNo;  }  /**   * Description of the Method   *   * @param theElement Description of Parameter   */  public void doMessage(TerminalManager theElement)  {    if (!forAnimator)    {      theElement.terminalFront(terminalNo);    }  }  /**   * Description of the Method   *   * @param theElement Description of Parameter   */  public void doMessage(TerminalManagerAnimator theElement)  {    if (forAnimator)    {      theElement.terminalFront(terminalNo);    }  }}