package org.rcosjava.messaging.messages.universal;import org.rcosjava.messaging.postoffices.animator.AnimatorMessageHandler;import org.rcosjava.messaging.postoffices.os.OSMessageHandler;import org.rcosjava.software.terminal.TerminalManager;/** * Toggle the terminal off or on. <P> * * * * @author Andrew Newman. * @created 28th March 1996 * @version 1.00 $Date$ */public class TerminalToggle extends UniversalMessageAdapter{  /**   * Description of the Field   */  private int iTerminalNo = 0;  /**   * Constructor for the TerminalToggle object   *   * @param theSource Description of Parameter   * @param iNewTerminalNo Description of Parameter   */  public TerminalToggle(OSMessageHandler theSource, int iNewTerminalNo)  {    super(theSource);    iTerminalNo = iNewTerminalNo;  }  /**   * Constructor for the TerminalToggle object   *   * @param theSource Description of Parameter   * @param iNewTerminalNo Description of Parameter   */  public TerminalToggle(AnimatorMessageHandler theSource, int iNewTerminalNo)  {    super(theSource);    iTerminalNo = iNewTerminalNo;  }  /**   * Sets the TerminalNumber attribute of the TerminalToggle object   *   * @param iNewTerminalNo The new TerminalNumber value   */  public void setTerminalNumber(int iNewTerminalNo)  {    iTerminalNo = iNewTerminalNo;  }  /**   * Description of the Method   *   * @param theElement Description of Parameter   */  public void doMessage(TerminalManager theElement)  {    theElement.terminalToggle(iTerminalNo);  }}