package org.rcosjava.messaging.messages.universal;import org.rcosjava.messaging.postoffices.os.OSMessageHandler;import org.rcosjava.software.animator.ipc.IPCManagerAnimator;import org.rcosjava.software.memory.MemoryManager;import org.rcosjava.software.memory.MemoryRequest;/** * Description of the Class * * @author administrator * @created 28 April 2002 */public class WritePage extends UniversalMessageAdapter{  /**   * Description of the Field   */  private MemoryRequest mrRequest;  /**   * Constructor for the WritePage object   *   * @param theSource Description of Parameter   * @param mrNewRequest Description of Parameter   */  public WritePage(OSMessageHandler theSource,      MemoryRequest mrNewRequest)  {    super(theSource);    mrRequest = mrNewRequest;  }  /**   * Sets the MemoryRequest attribute of the WritePage object   *   * @param mrNewRequest The new MemoryRequest value   */  public void setMemoryRequest(MemoryRequest mrNewRequest)  {    mrRequest = mrNewRequest;  }  /**   * Description of the Method   *   * @param theElement Description of Parameter   */  public void doMessage(IPCManagerAnimator theElement)  {    theElement.writingMemory(mrRequest);  }  /**   * Description of the Method   *   * @param theElement Description of Parameter   */  public void doMessage(MemoryManager theElement)  {    theElement.writePage(mrRequest.getPID(), mrRequest.getMemoryType(),        mrRequest.getOffset(), mrRequest.getMemory());    FinishedMemoryWrite aMessage = new FinishedMemoryWrite(theElement, mrRequest);    theElement.sendMessage(aMessage);  }}