package org.rcosjava.software.animator.help;import java.applet.*;import java.awt.*;import java.net.*;/** * Description of the Class * * @author administrator * @created 28 April 2002 */public class Help{  /**   * Constructor for the Help object   *   * @param myContext Description of Parameter   * @param myURL Description of Parameter   * @param where Description of Parameter   */  public Help(AppletContext myContext, String myURL, String where)  {    try    {      URL helpURL = new URL(myURL + where);      myContext.showDocument(helpURL, "RCOS_Online_Help_Window");    }    catch (MalformedURLException e)    {    }  }}