package org.rcosjava.software.animator.support;import java.awt.*;import java.awt.image.*;/** * Description of the Class * * @author administrator * @created 28 April 2002 */public class GreyOutImage extends RGBImageFilter{  /**   * Constructor for the GreyOutImage object   */  public GreyOutImage()  {    canFilterIndexColorModel = true;  }  /**   * Description of the Method   *   * @param iX Description of Parameter   * @param iY Description of Parameter   * @param iRGB Description of Parameter   * @return Description of the Returned Value   */  public int filterRGB(int iX, int iY, int iRGB)  {    DirectColorModel cm = (DirectColorModel) ColorModel.getRGBdefault();    int alpha = cm.getAlpha(iRGB);    int red = cm.getRed(iRGB);    int green = cm.getGreen(iRGB);    int blue = cm.getBlue(iRGB);    red = Math.max((int) (red - (red * 0.4)), 0);    green = Math.max((int) (green - (green * 0.4)), 0);    blue = Math.max((int) (blue - (blue * 0.4)), 0);    return alpha << 24 | red << 16 | green << 8 | blue;  }}