package org.rcosjava.software.animator.support;import java.awt.*;/** * Description of the Class * * @author administrator * @created 28 April 2002 */public class RCOSRectangle extends Canvas{  /**   * Description of the Field   */  private Color bgColour;  /**   * Description of the Field   */  private Color boxColour;  /**   * Description of the Field   */  private int iTopR, iLeftR, iBottomR, iRightR;  /**   * Constructor for the RCOSRectangle object   *   * @param iTop Description of Parameter   * @param iLeft Description of Parameter   * @param iBottom Description of Parameter   * @param iRight Description of Parameter   * @param bColour Description of Parameter   * @param bxColour Description of Parameter   */  public RCOSRectangle(int iTop, int iLeft, int iBottom, int iRight,      Color bColour, Color bxColour)  {    super();    iTopR = iTop;    iLeftR = iLeft;    iBottomR = iBottom;    iRightR = iRight;    bgColour = bColour;    boxColour = bxColour;    repaint();  }  /**   * Gets the MinimumSize attribute of the RCOSRectangle object   *   * @return The MinimumSize value   */  public Dimension getMinimumSize()  {    return getPreferredSize();  }  /**   * Gets the PreferredSize attribute of the RCOSRectangle object   *   * @return The PreferredSize value   */  public Dimension getPreferredSize()  {    return new Dimension(Math.abs(iTopR - iBottomR),        Math.abs(iLeftR - iRightR));  }  /**   * Adds a feature to the Notify attribute of the RCOSRectangle object   */  public void addNotify()  {    repaint();    super.addNotify();  }  /**   * Description of the Method   *   * @param g Description of Parameter   */  public void paint(Graphics g)  {    update(g);  }  /**   * Description of the Method   *   * @param g Description of Parameter   */  public void update(Graphics g)  {    g.setColor(bgColour);    g.fillRect(iTopR, iLeftR, iBottomR, iRightR);    g.setColor(boxColour);    g.drawRect(iTopR, iLeftR, iBottomR - 1, iRightR - 1);  }}