package org.rcosjava.software.animator.support.mtgos;/** * A chain is a simple linked list which contains an MTGO object and another * chain. <P> * * * * @author Andrew Newman. * @created 1st January 1997 * @version 1.00 $Date$ */public class Chain{  /**   * Description of the Field   */  public MTGO object;  /**   * Description of the Field   */  public Chain nextChain;  /**   * Constructor for the Chain object   *   * @param newObject Description of Parameter   * @param newNextChain Description of Parameter   */  public Chain(MTGO newObject, Chain newNextChain)  {    object = newObject;    nextChain = newNextChain;  }}